import React, { useEffect, useRef } from 'react';
import { MapContainer, TileLayer } from 'react-leaflet';
import "leaflet/dist/leaflet.css";

function Map() {
  const mapRef = useRef(null); // Refs to track map initialization
  
  const center = { lat: 51.505, lng: -0.09 };
  
  useEffect(() => {
    if (mapRef.current) {
      // Cleanup previous map instance before initializing a new one
      mapRef.current.leafletElement = null;
    }
  }, []); // Empty dependency array ensures this runs only on mount/unmount

  return (
    <MapContainer
      center={center}
      zoom={13}
      style={{ height: "500px", width: "100%" }}
      ref={mapRef}
      whenCreated={(map) => {
        console.log('Map instance created', map);  // Verify map instance is created
      }}
    >
      <TileLayer
        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
      />
    </MapContainer>
  );
}

export default Map;
